INSERT INTO Email_template(`email_template_id`, `template_subject`, `template_text`) VALUES (1, '[The Comment] Email Verification Check', "<div><br/><h1>The Comment 이메일 확인</h1><br/><p>안녕하세요. The Comment 입니다.<br>이메일 확인을 위해서 다음의 링크를 보내드립니다.</p><br/>		<a href=\"##{{url}}\">##{{url}}</a><br/><p>이 메일은 오직 발신 전용입니다. 답장하지 마세요.</p><br/>	</div><br/><div>-----------------------------------------</div><br/>	<div><br/>		<h1>The Comment Email verification</h1><br/>		<p>Hello. The Comment. In order to verify your email, we'll send you the following link.</p><br/>		<a href=\"##{{url}}\">##{{url}}</a><br/>		<p>This email is only for sending. Please do not reply.</p><br/>	</div>");