<node CREATED="1576249588668" ID="ID_928835042" MODIFIED="1576249588668" TEXT="private BookService bookService;"/>
<node CREATED="1576249588668" ID="ID_1570626075" MODIFIED="1576249588668" TEXT="@RequestMapping(value = &quot;/listBooks&quot;, method = RequestMethod.GET)"/>
<node CREATED="1576249588670" ID="ID_193083649" MODIFIED="1576249588670" TEXT="public String listBooks(Model model, @RequestParam(&quot;page&quot;) Optional&lt;Integer&gt; page, @RequestParam(&quot;size&quot;) Optional&lt;Integer&gt; size) {">
<node CREATED="1576249588673" ID="ID_1172351831" MODIFIED="1576249588673" TEXT="final int currentPage = page.orElse(1);"/>
<node CREATED="1576249588674" ID="ID_1007387719" MODIFIED="1576249588674" TEXT="final int pageSize = size.orElse(5);"/>
<node CREATED="1576249588674" ID="ID_402454947" MODIFIED="1576249588674" TEXT="Page&lt;Book&gt; bookPage = bookService.findPaginated(PageRequest.of(currentPage - 1, pageSize));"/>
<node CREATED="1576249588675" ID="ID_687415500" MODIFIED="1576249588675" TEXT="model.addAttribute(&quot;bookPage&quot;, bookPage);"/>
<node CREATED="1576249588675" ID="ID_853651805" MODIFIED="1576249588675" TEXT="int totalPages = bookPage.getTotalPages();"/>
<node CREATED="1576249588676" ID="ID_1323358737" MODIFIED="1576249588676" TEXT="if (totalPages &gt; 0) {">
<node CREATED="1576249588676" ID="ID_938547857" MODIFIED="1576249588676" TEXT="List&lt;Integer&gt; pageNumbers = IntStream.rangeClosed(1, totalPages)">
<node CREATED="1576249588676" ID="ID_1981694534" MODIFIED="1576249588676" TEXT=".boxed()"/>
<node CREATED="1576249588677" ID="ID_1839740150" MODIFIED="1576249588677" TEXT=".collect(Collectors.toList());"/>
</node>
<node CREATED="1576249588678" ID="ID_793042294" MODIFIED="1576249588678" TEXT="model.addAttribute(&quot;pageNumbers&quot;, pageNumbers);"/>
</node>
<node CREATED="1576249588678" ID="ID_88106383" MODIFIED="1576249588678" TEXT="}"/>
<node CREATED="1576249588679" ID="ID_1349428026" MODIFIED="1576249588679" TEXT="return &quot;listBooks.html&quot;;"/>
</node>
<node CREATED="1576249588679" ID="ID_1766534331" MODIFIED="1576249588679" TEXT="}"/>
</node>
